import React from 'react';
//import removeRepeated from './exer1';

class Pagina extends React.Component {
  constructor(props) {
    super(props);
    this.state = { palavra: props.palavra || '' };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeWord = this.handleChangeWord.bind(this);
  }

  handleChangeWord(event) {
    this.setState({
      palavra: event.target.palavra
    });
  }

  handleSubmit(event){
    alert('Um evento foi enviado: ' + this.state.palavra);

    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label>
            Palavra:
            <input type="text" value={this.state.palavra} onChange={(event) => {
                        this.handleChangeWord(event, this.state.palavra);
                    }}/>
          </label>
          <input type="submit" value="Enviar"/>
        </div>
      </form>
    )
  }
    //"xxyzzaayy"  
}

export default Pagina;
