import React, { useState } from 'react';
import removeRepeated from './exer1';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {palavra: '', limite: '', str: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      palavra: event.target.palavra,
      limite: event.target.limite, 
      str: event.target.str
    });
  }

  handleSubmit(event){
    event.preventDefault();

    str = removeRepeated({palavra},{limite});
    console.log(str);
  }
  render() {
    return (
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Palavra:
            <input type="text" value={palavra}/>
          </label>
        </div>
        <div>
          <label>
            Limite:
            <input type="text" value={limite} />
          </label>
        </div>
        <div>
          <input type="submit" />
          <label>{str}</label>
        </div>
      </form>
    )
  }
    //"xxyzzaayy"  
}
