/**
 * @param {string} string1 
 * @param {string} string2
 * @return {number}  
 */
const lettersSum = (string1, string2) => {
    var alfabeto ='abcdefghijklmnopqrtuvwyxz'
    var listaAlfabeto=[];
    for(let i=0;i<26;i++){
        listaAlfabeto.push(alfabeto[i]);
    }
    var menor;
    for(let i=1;i<string1.length;i++){
        var pos1 = listaAlfabeto.indexOf(string1[i]);
        var pos2 = listaAlfabeto.indexOf(string1[i-1]);
        if(pos1<pos2){
            menor=listaAlfabeto[pos1];
        }else{
            menor=listaAlfabeto[pos2];
        }
    }



    return menor;
}

export default lettersSum;
