import React from 'react';
import removeRepeated from './exer1';

class Pagina extends React.Component {
  constructor(props) {
    super(props);
    this.state = {palavra: '', limite: '', str: ''};

    this.handleChangeWord = this.handleChangeWord.bind(this);
    this.handleChangeLimit = this.handleChangeLimit.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChangeWord(event) {
    this.setState({
      palavra: event.target.palavra
    });
  }

  handleChangeLimit(event) {
    this.setState({
      limite: event.target.limite
    });
  }

  handleSubmit(event){
    event.preventDefault();
    this.str = removeRepeated(String(this.palavra),Number(this.limite));
    //
    console.log(`Valor final: ${String(this.str)}`);
    console.log(`Valor palavra: ${String(this.palavra)}`);
    console.log(`Valor limite: ${String(this.limite)}`);
  }

  render() {
    var str = this.state.str;
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label>
            Palavra:
            <input type="text" value={this.state.palavra} onChange={this.handleChangeWord}/>
          </label>
        </div>
        <div>
          <label>
            Limite:
            <input type="text" value={this.state.limite} onChange={this.handleChangeLimit}/>
          </label>
        </div>
        <div>
          <input type="submit" value="Enviar"/>
          <a>{str? str:"NO"}</a>
        </div>
      </form>
    )
  }
    //"xxyzzaayy"  
}

export default Pagina;
