  constructor(props) {
    super(props);
    this.state = {palavra: '', limite: '', str: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange(event) {
    this.setState({
      palavra: event.target.palavra,
      limite: event.target.limite, 
      str: event.target.str
    });

  }

  handleSubmit(event){
    event.preventDefault();

    //
    console.log(String(this.str));
  }
  handleClick(e) {
    e.preventDefault();
    this.str = removeRepeated(String(this.palavra),Number(this.limite));
  }

  render() {
    var str = this.state.str;
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label>
            Palavra:
            <input type="text" value={this.state.palavra} onChange={this.handleChange}/>
          </label>
        </div>
        <div>
          <label>
            Limite:
            <input type="text" value={this.state.limite} onChange={this.handleChange}/>
          </label>
        </div>
        <div>
          <input type="submit" value="Enviar" onClick={handleClick}/>
          <a>{str? str:"NO"}</a>
        </div>
      </form>
    )
  }
